$(document).ready(function() {

	// Replace png with svg using modernizr
	if (!Modernizr.svg) {
		$('img[src*="svg"]').attr('src', function() {
			return $(this).attr('src').replace('.svg', '.png');
		});
	}
	//--> svg replace


  // Columnizer polyfill for css3 columns
  if (!Modernizr.csscolumns) {
    $('.columns').columnize();
  }
  // --> Columnizer


  // MixItUp for sorting & filtering
  $(function () {
    $('.columns').mixitup({
      targetSelector: '.column__item',
      filterSelector: '.collage__filter a'
    });
  })
  //--> MixItUp


  // init fluidbox
  $(function () {
    // set up animations
    $('.collage__img').data('animation', {
      next: {
        in: 'fadeInRight',
        out: 'fadeOutLeft'
      },
      prev: {
        in: 'fadeInLeft',
        out: 'fadeOutRight'
      }
    });

    // Call plugin
    $('.collage__img').fluidbox();
  });
  //--> fluidbox

});//doc ready
